const person_entity_ids = msg.person_entities.map((person) => person.entity_id);

return {
    ...msg,
    persons: person_entity_ids
};

const color = msg.persons.length > 0 ? "green": "red";
const text = msg.persons.join(', ')

return {
    payload: {
        fill: color,
        text
    }
};

// Define inputs
const env_keys = [];

// Where should the parsed inputs be stored during the subflow
const storage = 'flow_storage';

// Save the msg
flow.set('input_msg', JSON.stringify(msg));

msg[storage] = {};

// Check if string contains mustache
const containsMustache = (str) => {
    const regex = /{{(?:(?!}}).+)}}/g;
    return regex.test(str);
}

// Parse inputs
env_keys.forEach((env_key) => {
    let env_value = env.get(env_key);
    if (containsMustache(env_value)) {
        env_value = env_value.replace(/(\{{)\s*(\S+)\s*(\}})/g, "$2");
        env_value = selectn(env_value, msg);
    }
    msg[storage][env_key] = env_value;
});

return msg;

// Define outputs
// * Key - environment variable
// * Value - msg path to value
const outputs = {
    'output_persons': 'persons'
};

const buildObjWithValue = (path, value = '') => {
    const paths = path.split('.');
    return paths.reduceRight((acc, item, index) => ({
        [item]: index === paths.length - 1
            ? value
            : acc
    }), {});
}

// Set output variables
const output_values = {};
Object.keys(outputs).forEach((output_key) => {
    const output_value_path = outputs[output_key];
    const output_value_location = env.get(output_key);
    const output_value = selectn(output_value_path, msg)
    if (output_value_location && output_value_location !== "") {
        Object.assign(output_values, buildObjWithValue(output_value_location, output_value));
    }
});

// Load the msg as the input was to the subflow
const pre_msg = JSON.parse(flow.get('input_msg'));

// Return the input message alongside the outputs
msg = Object.assign(pre_msg, output_values);
return msg;
